<?php/** * WPB WooCommerce Product slider * By WpBean */if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly/** * Adding modernizr */if( !function_exists('wpb_wps_modernizr') ):	function wpb_wps_modernizr() {		if (!is_admin()) {			wp_deregister_script('modernizr');			wp_enqueue_script('wp_enqueue_scripts', WPB_WPS_URI.'/assets/js/modernizr.custom.js', false, '2.7.0', false);		}		}endif;add_action('wp_enqueue_scripts', 'wpb_wps_modernizr');/** * Adding scripts */if( !function_exists('wpb_wps_adding_scripts') ):	function wpb_wps_adding_scripts() {		wp_enqueue_script('wpb-wps-toucheffects', WPB_WPS_URI.'/assets/js/toucheffects.js', '','1.0', true);		wp_enqueue_script('wpb-wps-owl-carousel', WPB_WPS_URI.'/assets/js/owl.carousel.js', array('jquery'),'1.3.2', true);	}endif;add_action( 'wp_enqueue_scripts', 'wpb_wps_adding_scripts' ); /** * Adding Styles */if( !function_exists('wpb_wps_adding_style') ):	function wpb_wps_adding_style() {		wp_enqueue_style( 'wpb_wps_google_fonts', '//fonts.googleapis.com/css?family=Roboto:300,100,400', false );		wp_enqueue_style('wpb_wps_owl_carousel', WPB_WPS_URI.'/assets/css/owl.carousel.css','','1.0', false);		wp_enqueue_style('wpb_wps_main_style', WPB_WPS_URI.'/assets/css/main.css', '','1.0', false);	}endif;add_action( 'wp_enqueue_scripts', 'wpb_wps_adding_style' );/** * Enqueue CSS For Admin */if( !function_exists('wpb_wps_adding_admin_style') ):	function wpb_wps_adding_admin_style() {		$screen = get_current_screen();		if( $screen->id == 'settings_page_wpb_woocommerce_product_slider' ){			wp_enqueue_style('wpb_wps_admin_style', WPB_WPS_URI.'/assets/css/admin-style.css', '','1.0', false);		}	}endif;add_action( 'admin_enqueue_scripts', 'wpb_wps_adding_admin_style',11 );