<?php/** * WPB WooCommerce Product slider * By WpBean *//** * Adding the widgets */add_action('widgets_init','wpb_wps_register_widgets');function wpb_wps_register_widgets(){    register_widget('wpb_wps_latest_product');    register_widget('wpb_wps_feature_product');}/** * Latest Products Slider Widget */class wpb_wps_latest_product extends wp_widget {	function __construct(){		$params =   array(			'description' => __( 'Display a latest products slider on your sidebar.','wpb-wps' ),            'name'        => __( 'WPB Latest Products Slider','wpb-wps' ),		);		parent::__construct('wpb_wps_latest_product','',$params);	}	    public function  form($instance){        extract($instance);        if( !isset( $number_of_product ) ){			$number_of_product = 5;		}		?>	        <p>	            <label for="<?php echo $this->get_field_id('title');?>"><?php _e( 'Title:','wpb-wps' );?></label>	            <input class="widefat" id="<?php echo $this->get_field_id('title');?>" name="<?php echo $this->get_field_name('title');?>" value="<?php if(isset($title)) echo esc_attr($title);?>"/>			</p>			<p> 				<label for="<?php echo $this->get_field_id('number_of_product');?>"><?php _e( 'Number of Products:','wpb-wps' );?></label>				<input style="max-width:30%;display:block;" class="widefat" type="number" min="1" id="<?php echo $this->get_field_id('number_of_product');?>" name="<?php echo $this->get_field_name('number_of_product');?>" value="<?php echo esc_attr($number_of_product); ?>"/>			</p>		<?php	}    public function widget($args,$instance){				extract($args);		extract($instance);				echo $before_widget;			echo $before_title.$title.$after_title;			echo do_shortcode("[wpb-sidebar-latest-product posts=$number_of_product]");		echo $after_widget;	    }}/** * Featured Product Slider */class wpb_wps_feature_product extends wp_widget {	function __construct(){		$params =   array(			'description' => __( 'Display a feature products slider on your sidebar.','wpb-wps' ),            'name'        => __( 'WPB Feature Products Slider','wpb-wps' ),		);		parent::__construct('wpb_wps_feature_product','',$params);	}	    public function  form($instance){        extract($instance);        if( !isset( $number_of_product ) ){			$number_of_product = 5;		}		?>	        <p>	            <label for="<?php echo $this->get_field_id('title');?>"><?php _e( 'Title:','wpb-wps' );?></label>	            <input class="widefat" id="<?php echo $this->get_field_id('title');?>" name="<?php echo $this->get_field_name('title');?>" value="<?php if(isset($title)) echo esc_attr($title);?>"/>			</p>			<p> 				<label for="<?php echo $this->get_field_id('number_of_product');?>"><?php _e( 'Number of Products:','wpb-wps' );?></label>				<input style="max-width:30%;display:block;" class="widefat" type="number" min="1" id="<?php echo $this->get_field_id('number_of_product');?>" name="<?php echo $this->get_field_name('number_of_product');?>" value="<?php echo esc_attr($number_of_product);?>"/>			</p>		<?php    }    public function widget($args,$instance){		extract($args);		extract($instance);				echo $before_widget;			echo $before_title.$title.$after_title;			echo do_shortcode("[wpb-sidebar-feature-product posts=$number_of_product]");		echo $after_widget;		}}